import React, { useState } from 'react';

function Register() {
  const [userProfile, setUserProfile] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
    profilePicture: null,
  });

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(userProfile);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserProfile({ ...userProfile, [name]: value });
  };

  return (
    <div className="bg-gradient-to-r from-purple-500 to-orange-500 min-h-screen flex justify-center items-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-3xl font-bold text-center mb-4">Register on Your Website</h1>
        <p className="text-lg mb-8">Fill in the form below to create an account.</p>
        <form onSubmit={handleFormSubmit}>
          <div className="mb-4">
            <label htmlFor="firstName" className="block font-medium">First Name</label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              className="w-full px-3 py-2 border rounded-lg"
              value={userProfile.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          {/* Add other form fields here */}
          <button
            type="submit"
            className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full w-full"
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
}

export default Register;
