import React, { useState } from 'react';
import ProfileForm from './ProfileForm';

function AdminPanel() {
  const [users, setUsers] = useState(JSON.parse(sessionStorage.getItem('registeredUsers')) || []);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const bannedUsers = JSON.parse(sessionStorage.getItem('bannedUsers')) || [];
    const activeUsers = storedUsers.filter(user => !bannedUsers.find(bannedUser => bannedUser.email === user.email));
    setUsers(activeUsers);
  }, []);

  const handleBanUser = (email) => {
    // Implement user banning logic (e.g., remove user from users array)
    const updatedUsers = users.filter(user => user.email !== email);
    setUsers(updatedUsers);

    // Move the banned user to a separate array (you can adjust the name as needed)
    const bannedUsers = JSON.parse(sessionStorage.getItem('bannedUsers')) || [];
    const userToBan = users.find(user => user.email === email);
    bannedUsers.push(userToBan);
    sessionStorage.setItem('bannedUsers', JSON.stringify(bannedUsers));
  };

  const handleUnbanUser = (email) => {
    // Implement user unbanning logic (e.g., move user back to users array)
    const updatedBannedUsers = users.filter(user => user.email === email);
    const unbannedUser = updatedBannedUsers[0];

    const updatedUsers = [...users, unbannedUser];
    setUsers(updatedUsers);

    // Remove the unbanned user from the banned users array
    const bannedUsers = JSON.parse(sessionStorage.getItem('bannedUsers')) || [];
    const updatedBannedUsersArray = bannedUsers.filter(user => user.email !== email);
    sessionStorage.setItem('bannedUsers', JSON.stringify(updatedBannedUsersArray));
  };

  const handleEmulateUser = (email) => {
    // Implement user emulation logic (e.g., get user's profile data)
    const userToEmulate = users.find(user => user.email === email);
    setSelectedUser(userToEmulate);
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-8 bg-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-semibold mb-4">Admin Panel</h1>
      <ul>
        {users.map((user) => (
          <li key={user.email} className="flex items-center justify-between border-b py-2">
            <div>{user.email}</div>
            <div>
              <button
                onClick={() => handleBanUser(user.email)}
                className="bg-red-500 hover:bg-red-600 text-white px-2 py-1 rounded-full mr-2"
              >
                Ban
              </button>
              <button
                onClick={() => handleUnbanUser(user.email)}
                className="bg-green-500 hover:bg-green-600 text-white px-2 py-1 rounded-full mr-2"
              >
                Unban
              </button>
              <button
                onClick={() => handleEmulateUser(user.email)}
                className="bg-blue-500 hover:bg-blue-600 text-white px-2 py-1 rounded-full"
              >
                Emulate
              </button>
            </div>
          </li>
        ))}
      </ul>
      {/* Rest of the component */}
    </div>
  );
}

export default AdminPanel;
