import React, { useState, useEffect } from 'react';
import InputValidator from './InputValidator'; // Import the utility

function ProfileForm({ user, editMode, onEditToggle, onSaveProfile, onPasswordChange, onInputChange, onPasswordInputChange, onImageChange }) {
  const [passwordError, setPasswordError] = useState(null);
  const [imageError, setImageError] = useState(null);

  const handleSaveProfile = () => {
    onSaveProfile();
  };

  const handlePasswordChange = () => {
    onPasswordChange();
  };

  const handleImageChange = (e) => {
    onImageChange(e);
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-8 bg-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-semibold mb-4">Your Profile</h1>
      <div className="grid grid-cols-2 gap-4">
        <div className="mb-4 col-span-2">
          <label htmlFor="profilePicture" className="block text-sm font-medium text-gray-700">
            Profile Picture
          </label>
          {editMode ? (
            <input
              type="file"
              id="profilePicture"
              name="profilePicture"
              accept="image/*"
              onChange={handleImageChange}
              className="w-full px-4 py-2 border rounded-lg"
            />
          ) : (
            <img
              src={user.profilePicture}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover"
            />
          )}
          {imageError && <p className="text-red-500 mt-2">{imageError}</p>}
        </div>
        {/* Other form fields for editing profile */}
        <div className="col-span-2">
          <div className="mb-4">
            <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
              First Name
            </label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={user.firstName}
              onChange={onInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              disabled={!editMode}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
              Last Name
            </label>
            <input
              type="text"
              id="lastName"
              name="lastName"
              value={user.lastName}
              onChange={onInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              disabled={!editMode}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700">
              Phone Number
            </label>
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              value={user.phoneNumber}
              onChange={onInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              disabled={!editMode}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="dateOfBirth" className="block text-sm font-medium text-gray-700">
              Date of Birth
            </label>
            <input
              type="date"
              id="dateOfBirth"
              name="dateOfBirth"
              value={user.dateOfBirth || ''}
              onChange={onInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              disabled={!editMode}
            />
          </div>
        </div>
      </div>
      {/* Display user profile data */}
      <button onClick={onEditToggle} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full">
        {editMode ? 'Cancel Edit' : 'Edit Profile'}
      </button>
      {editMode && (
        <div className="mt-4">
          <button onClick={handleSaveProfile} className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full mr-4">
            Save Profile
          </button>
          {/* Password Change Section */}
          <div className="mb-4">
            <label htmlFor="currentPassword" className="block text-sm font-medium text-gray-700">
              Current Password
            </label>
            <input
              type="password"
              id="currentPassword"
              name="currentPassword"
              value={user.passwordData.currentPassword}
              onChange={onPasswordInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              required
            />
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div className="mb-4">
              <label htmlFor="newPassword" className="block text-sm font-medium text-gray-700">
                New Password
              </label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={user.passwordData.newPassword}
                onChange={onPasswordInputChange}
                className="w-full px-4 py-2 border rounded-lg"
                required
              />
            </div>
            <div className="mb-4">
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
                Confirm Password
              </label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={user.passwordData.confirmPassword}
                onChange={onPasswordInputChange}
                className="w-full px-4 py-2 border rounded-lg"
                required
              />
            </div>
          </div>
          {passwordError && <p className="text-red-500 mb-4">{passwordError}</p>}
          <button onClick={handlePasswordChange} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full">
            Change Password
          </button>
        </div>
      )}
    </div>
  );
}

export default ProfileForm;
