import React, { useState } from 'react';

function Register() {
  const [registerData, setRegisterData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
  });

  const [validationError, setValidationError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRegisterData({ ...registerData, [name]: value });
  };

  const isEmailValid = (email) => {
    // Simple email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const isPasswordValid = (password) => {
    // Password validation (10 minimal length, one capital letter, one number, one special character)
    const passwordRegex = /^(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*])(.{10,})$/;
    return passwordRegex.test(password);
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();

    // Validation checks
    if (!isEmailValid(registerData.email)) {
      setValidationError('Invalid email address');
      return;
    }

    if (!isPasswordValid(registerData.password)) {
      setValidationError(
        'Password must be at least 10 characters long and include at least one capital letter, one number, and one special character.'
      );
      return;
    }

    // Storing data (for now, in-memory storage)
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    storedUsers.push(registerData);
    localStorage.setItem('registeredUsers', JSON.stringify(storedUsers));

    // Reset the form
    setRegisterData({
      firstName: '',
      lastName: '',
      email: '',
      phoneNumber: '',
      password: '',
      confirmPassword: '',
    });

    setValidationError(null);

    console.log('Registration Data:', registerData);
  };

  return (
    <form onSubmit={handleRegisterSubmit}>
      {validationError && <div className="text-red-500 mb-4">{validationError}</div>}
      <div className="mb-4">
        <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
          First Name
        </label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          value={registerData.firstName}
          onChange={handleInputChange}
          className="w-full px-4 py-2 border rounded-lg"
          required
        />
      </div>
      {/* Add other registration fields here */}
      <button
        type="submit"
        className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full w-full"
      >
        Register
      </button>
    </form>
  );
}

export default Register;
