import React, { useState } from 'react';
import Login from './Login';
import Register from './Register';

function Home() {
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const toggleForm = () => {
    setShowRegisterForm(!showRegisterForm);
  };

    // Initialize users array with a default admin
    const initialUsers = [
      {
        email: 'admin@bvwa.net',
        password: 'adm1ni$tratoR',
        firstName: 'Admin',
        lastName: 'User',
        phoneNumber: '123456789',
        dateOfBirth: '1990-01-01',
        profilePicture: 'default-profile-pic.jpg', // You can add a default image URL
      },
    ];
  
    // Initialize registeredUsers in localStorage with the default admin
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || initialUsers;
    localStorage.setItem('registeredUsers', JSON.stringify(storedUsers));
  
    // Additional initialization for isAdmin in sessionStorage
    const isAdmin = JSON.parse(localStorage.getItem('isAdmin')) || false;

  return (
    <div className="bg-gradient-to-r from-purple-500 to-orange-500 min-h-screen flex justify-center items-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-3xl font-bold text-center mb-4">
          {showRegisterForm ? 'Register on Your Website' : 'Login to Your Account'}
        </h1>
        {showRegisterForm ? (
          <Register />
        ) : (
          <Login />
        )}
        <button
          onClick={toggleForm}
          className="text-purple-500 hover:underline mt-4 block text-center cursor-pointer"
        >
          {showRegisterForm ? 'Already registered? Login here.' : 'Not registered? Register here.'}
        </button>
      </div>
    </div>
  );
}

export default Home;
