import React, { useState, useEffect } from 'react';

function Profile({ loggedInUser }) {
  const [editMode, setEditMode] = useState(false);
  const [userProfile, setUserProfile] = useState({ ...loggedInUser });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });
  const [passwordError, setPasswordError] = useState(null);

  useEffect(() => {
    setUserProfile({ ...loggedInUser });
  }, [loggedInUser]);

  const handleEditToggle = () => {
    setEditMode(!editMode);
  };

  const handleSaveProfile = () => {
    // Save profile logic (e.g., update user data in localStorage or send to server)
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const updatedUsers = storedUsers.map((u) => (u.email === loggedInUser.email ? userProfile : u));
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
    setEditMode(false);
  };

  const handlePasswordChange = () => {
    // Password change logic (e.g., update password in localStorage or send to server)
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const user = storedUsers.find((u) => u.email === loggedInUser.email);

    if (user.password === passwordData.currentPassword) {
      if (passwordData.newPassword === passwordData.confirmPassword) {
        user.password = passwordData.newPassword;
        const updatedUsers = storedUsers.map((u) => (u.email === loggedInUser.email ? user : u));
        localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
        setPasswordError(null);
      } else {
        setPasswordError('New password and confirm password do not match');
      }
    } else {
      setPasswordError('Current password is incorrect');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserProfile({ ...userProfile, [name]: value });
  };

  const handlePasswordInputChange = (e) => {
    const { name, value } = e.target;
    setPasswordData({ ...passwordData, [name]: value });
  };

  return (
    <div>
      <h1 className="text-3xl font-semibold mb-4">Your Profile</h1>
      {editMode ? (
        <div>
          <h2 className="text-xl mb-2">Edit Profile</h2>
          <div className="mb-4">
            <label htmlFor="profilePicture" className="block text-sm font-medium text-gray-700">
              Profile Picture
            </label>
            <input
              type="text"
              id="profilePicture"
              name="profilePicture"
              value={userProfile.profilePicture}
              onChange={handleInputChange}
              className="w-full px-4 py-2 border rounded-lg"
            />
          </div>
          <div className="grid grid-cols-2 gap-4">
            {/* Name */}
            <div className="mb-4">
              <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={userProfile.name}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg"
              />
            </div>
            {/* Date of Birth */}
            <div className="mb-4">
              <label htmlFor="dateOfBirth" className="block text-sm font-medium text-gray-700">
                Date of Birth
              </label>
              <input
                type="date"
                id="dateOfBirth"
                name="dateOfBirth"
                value={userProfile.dateOfBirth}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg"
              />
            </div>
          </div>
          <div className="grid grid-cols-2 gap-4">
            {/* Sex */}
            <div className="mb-4">
              <label htmlFor="sex" className="block text-sm font-medium text-gray-700">
                Sex
              </label>
              <select
                id="sex"
                name="sex"
                value={userProfile.sex}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg"
              >
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
            {/* Phone Number */}
            <div className="mb-4">
              <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700">
                Phone Number
              </label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={userProfile.phoneNumber}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg"
              />
            </div>
          </div>
          {/* Add other form fields for editing profile */}
          <button onClick={handleSaveProfile} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full">
            Save Profile
          </button>
        </div>
      ) : (
        <div>
          <h2 className="text-xl mb-2">View Profile</h2>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Profile Picture</label>
            <img
              src={userProfile.profilePicture}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover"
            />
          </div>
          <div className="grid grid-cols-2 gap-4 mb-4">
            {/* Display other user profile data */}
            <div>
              <label className="block text-sm font-medium text-gray-700">Name</label>
              <p className="text-gray-800">{userProfile.name}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Date of Birth</label>
              <p className="text-gray-800">{userProfile.dateOfBirth}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Sex</label>
              <p className="text-gray-800">{userProfile.sex}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Phone Number</label>
              <p className="text-gray-800">{userProfile.phoneNumber}</p>
            </div>
          </div>
          {/* Display other user profile data */}
          <button onClick={handleEditToggle} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full">
            Edit Profile
          </button>
        </div>
      )}
      <hr className="my-6" />
      {/* Password Change Section */}
      <div>
        <h2 className="text-xl mb-2">Change Password</h2>
        <div className="mb-4">
          <label htmlFor="currentPassword" className="block text-sm font-medium text-gray-700">
            Current Password
          </label>
          <input
            type="password"
            id="currentPassword"
            name="currentPassword"
            value={passwordData.currentPassword}
            onChange={handlePasswordInputChange}
            className="w-full px-4 py-2 border rounded-lg"
            required
          />
        </div>
        <div className="grid grid-cols-2 gap-4">
          <div className="mb-4">
            <label htmlFor="newPassword" className="block text-sm font-medium text-gray-700">
              New Password
            </label>
            <input
              type="password"
              id="newPassword"
              name="newPassword"
              value={passwordData.newPassword}
              onChange={handlePasswordInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
              Confirm Password
            </label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={passwordData.confirmPassword}
              onChange={handlePasswordInputChange}
              className="w-full px-4 py-2 border rounded-lg"
              required
            />
          </div>
        </div>
        {passwordError && <p className="text-red-500 mb-4">{passwordError}</p>}
        <button onClick={handlePasswordChange} className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full">
          Change Password
        </button>
      </div>
    </div>
  );
}

export default Profile;
