import React, { useState } from 'react';
import Register from './Register'; // Import the Register component

function Home() {
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const toggleForm = () => {
    setShowRegisterForm(!showRegisterForm);
  };

  return (
    <div className="bg-gradient-to-r from-purple-500 to-orange-500 min-h-screen flex justify-center items-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-3xl font-bold text-center mb-4">Welcome to Your Website</h1>
        {showRegisterForm ? (
          <Register toggleForm={toggleForm} />
        ) : (
          <>
            <p className="text-lg mb-8">Please log in with your email and password.</p>
            {/* Add your login form here */}
            <button
              onClick={toggleForm}
              className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-full w-full"
            >
              Not registered?
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
