import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Header() {
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  const handleLogout = () => {
    // Add logic to clear user data and navigate to the home page
    localStorage.removeItem('isLoggedIn');
    navigate('/');
  };

  return (
    <header className="bg-purple-500 py-4">
      <nav className="container mx-auto flex items-center justify-between">
        <div className="text-white text-2xl font-bold">Your Logo</div>
        {isLoggedIn ? (
          <div className="flex items-center">
            {/* Display profile picture here */}
            <div className="rounded-full overflow-hidden h-10 w-10 bg-gray-300 mr-2">
              {/* Insert logic to display user's profile picture */}
            </div>
            {/* Display user's first name */}
            <div className="text-white mr-2">Hi, John</div>
            {/* Clickable button to go to home/index */}
            <Link to="/" className="text-white hover:text-purple-300">Home</Link>
            {/* Logout button */}
            <button onClick={handleLogout} className="text-white hover:text-purple-300 ml-4">Logout</button>
          </div>
        ) : (
          <ul className="space-x-4">
            {/* Clickable button to go to home/index */}
            <li><Link to="/" className="text-white hover:text-purple-300">Home</Link></li>
          </ul>
        )}
      </nav>
    </header>
  );
}

export default Header;
